import React from 'react';
import close_overlay from '../images/close.svg';
// import "../styles/mobile_navbar.css";


class MobileNavbar extends React.Component{

    state = {
        is_authenticated: false,
        user:{
            username: "None",
        }
    }

    get_navigation = () => {
        if(this.state.is_authenticated){
            return(     
                <ul id="overlay-navigation">
                    <li class="overlay-navigation-index"><a href="" class="overlay-navlink"><img class="overlay-navlink-icon" src="{% static 'posts/img/account.svg' %}"/> <p>Account</p></a></li>    
                    <li class="overlay-navigation-index"><a href="{% url 'upload_post' %}" class="overlay-navlink"><img class="overlay-navlink-icon" src="{% static 'posts/img/account.svg' %}"/> <p>Upload</p></a></li>
                    <li class="overlay-navigation-index"><a href="" class="overlay-navlink"><img class="overlay-navlink-icon" src="{% static 'posts/img/settings.svg' %}"/> <p>Settings</p></a></li>
                    <li class="overlay-navigation-index"><a href="{% url 'logout' %}?to={{request.get_full_path}}" class="overlay-navlink"><img class="overlay-navlink-icon" src="{% static 'posts/img/logout.svg' %}"/> <p>Log out</p></a></li>
                </ul>
            );
        }
        else{
            return(   
                <ul id="overlay-navigation">
                    <li class="overlay-navigation-index"><a href="" class="overlay-navlink"><img class="overlay-navlink-icon" src="{% static 'posts/img/login.svg' %}"/> <p>Login</p></a></li>
                </ul>
            );
        }
    }

    close_overlay = () =>{
        this.overlayMenu.classList.add("overlay-menu-disabled");
    }
    
    render(){
        return(
            <div class="overlay-menu  overlay-menu-disabled" ref={(ref) => this.overlayMenu = ref}>

                <div id="overlay-menu-header">
                    <img src="{{request.user.profile_photo.url}}" alt="profile photo" id="overlay-menu-profile-photo"/>
                    <div id="overlay-menu-profile-info">
                        <p id="overlay-menu-profile-username">{this.state.user.username}</p>
                    </div>
                    <img src={close_overlay} alt="Close Icon" id="close-overlay-menu"/>
                </div>

                {this.get_navigation()}
        </div>
        );
    }

}

export default MobileNavbar;
