import React from 'react';
import close_overlay from '../images/close.svg';
import account_icon from "../images/account.svg";
import login_icon from "../images/login.svg";
import logout_icon from "../images/logout.svg";
import settings_icon from "../images/settings.svg";
import {Link} from "react-router-dom";
import {connect} from "react-redux";

class MobileNavbar extends React.Component{

    state = {
        is_authenticated: true,
        account: this.props.account,
    }

    get_navigation = () => {
        if(this.state.is_authenticated){
            return(     
                <ul id="overlay-navigation">
                    <li class="overlay-navigation-index"><a href="" class="overlay-navlink"><img class="overlay-navlink-icon" src={account_icon}/> <p>Account</p></a></li>    
                    <li class="overlay-navigation-index"><a href="{% url 'upload_post' %}" class="overlay-navlink"><img class="overlay-navlink-icon" src={account_icon} /> <p>Upload</p></a></li>
                    <li class="overlay-navigation-index"><a href="" class="overlay-navlink"><img class="overlay-navlink-icon" src={settings_icon} /> <p>Settings</p></a></li>
                    <li class="overlay-navigation-index"><a href="{% url 'logout' %}?to={{request.get_full_path}}" class="overlay-navlink"><img class="overlay-navlink-icon" src={logout_icon}/> <p>Log out</p></a></li>
                </ul>
            );
        }
        else{
            return(   
                <ul id="overlay-navigation">
                    <li class="overlay-navigation-index"><a href="" class="overlay-navlink"><img class="overlay-navlink-icon" src={login_icon} /> <p>Login</p></a></li>
                </ul>
            );
        }
    }

    close_overlay = () =>{
        this.overlayMenu.classList.add("overlay-menu-disabled");
    }
    
    render(){
        return(
            <div class="overlay-menu  overlay-menu-disabled" ref={(ref) => this.overlayMenu = ref}>

                <div id="overlay-menu-header">
                    <img src="{{request.user.profile_photo.url}}" alt="profile photo" id="overlay-menu-profile-photo"/>
                    <div id="overlay-menu-profile-info">
                        <p id="overlay-menu-profile-username">{this.state.user.username}</p>
                    </div>
                    <img src={close_overlay} alt="Close Icon" id="close-overlay-menu" onClick={this.close_overlay}/>
                </div>

                {this.get_navigation()}
        </div>
        );
    }

}

function mapStateToProps(state, oldProps){
    return{
        account: state.account,
    }
}

export default connect(mapStateToProps)(MobileNavbar);
