import React from "react";
import "../styles/navbar.css";
import menu_icon from "../images/menu.svg";
import brand_logo from "../images/brand_logo.svg";
import MobileNavbar from "./mobile_navbar.js"
import {connect} from "react-redux";


class Navbar extends React.Component{

    state = {
        is_authenticated: false,
        account: this.props.account,
    }

    get_navigation = () => {
        if(this.state.is_authenticated){
            return(
                <ul id="navbar-navigation">          
                    <li class="navigation-index"><a href="" class="navlink">Posts</a></li>
                    <li class="navigation-index"><a href="{% url 'upload_post' %}" class="navlink">Upload</a></li>
                    <li class="navigation-index"><a href="{% url 'logout' %}?to={{request.path}}" class="navlink">Log out</a></li>
                    <a href=""><img id="navigation-profile-photo" src="{{request.user.profile_photo.url}}" alt="" /></a>
                 </ul>
            );
        }
        else{
            return(
                <ul id="navbar-navigation">          
                    <li class="navigation-index"><Link to="/login" className="navlink">Log In</Link></li>
                </ul>
            );
        }
    }

    open_overlay = () =>{
        this.mobileNavbar.overlayMenu.classList.remove("overlay-menu-disabled");
    }

    render(){
        return(
            <nav id="navbar">
                <div id="navbar-brand-container" class="centered">
                    <img src={brand_logo} alt="Brand Img" id="brand-logo" />
                </div>
                {this.get_navigation()}
                <img src={menu_icon} alt="Logo" id="open-overlay-menu" onClick={this.open_overlay}/>

                <MobileNavbar ref={(ref) => this.mobileNavbar = ref} account={this.state.account}/>
            </nav>
        );
    }
}

function setStateToProps(state, oldProps){
    return{
        account: state.account,
    }
}

export default connect(setStateToProps)(Navbar);
