import Cookies from 'universal-cookie';
import axios from 'axios';

let cookie = new Cookies();
let token = cookie.get("token");
console.log("Cookei Token", token);

let account;

let data;

if(token !== undefined){
    axios.get(`http://localhost/api/user?token=${token}`)
    .then((response) => {
        console.log("Reducer Account Response", response.data);
        account = response.data;
        console.log("Reducer Account", account);

        data = {
            account: account,
            token: token,
        }
    })
    .catch((error) => {
        console.log(error);
    });
    
}else{
    data = {
        token: null,
        account: null
    }
}



function main_reducer(state=data, action){

    if(action.type == 'set_token'){
        console.log("set token");
        return{
            ...state,
            token: action.data
        }
    }

    if(action.type == "set_account"){
        console.log("set account");
        return{
            ...state,
            account: action.data,
        }
    }

    if(action.type == "set_is_auth" ){
        console.log("set auth");
        return{
            ...state,
            is_authenticated: action.data
        }
    }

    return state;
}


export default main_reducer;
